'use strict';


var TackCons = function(){
    var self = this;
    self.info = [
        {id:0, code:'recheck', des:'复核'},
        {id:1, code:'calculate', des:'计算利息'}
    ];
    self.infoArray = {};
    self.init();
};

TackCons.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

TackCons.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new TackCons();
