/**
 * @param headNode
 * @param bodyNode
 * @param cb
 * @Description  CM10 获取银行开户地区代码信息
 */

CommonControl.prototype.checkCM10 = function (headNode, bodyNode, cb) {
    cb(null);
};

CommonControl.prototype.handleCM10 = function (headNode, bodyNode, cb) {
    var conn = dc.main.getConn();
    var sql = "";
    if (bodyNode.proCode == undefined) {
        sql = "select a.pro_code,a.province from fuiou_city a GROUP BY a.province ORDER BY city_code";
    } else {
        sql = "select a.city_code,a.city from fuiou_city a where a.pro_code='" + bodyNode.proCode + "' ORDER BY city_code";
    }
    conn.execute(sql, [], function (err, data) {
        var backNode = {};
        backNode.rst = data;
        cb(err, backNode);
    })

};


