'use strict';
var cons = require('mcp_constants');
var calCons = cons.calculateCons;


var esut = require('easy_util');
var dateUtil = esut.dateUtil;

var util = require('mcp_util');
var dateMathUtil = util.dateMathUtil;

var CreditorCalculateService = function () {
};

/**
 * 等额本息
 * 债权价值
 */
CreditorCalculateService.prototype.creditorValueDeMonth = function (curRepayDay, curDay, preRepayDay, money, interest) {
    return Math.round(money +((dateMathUtil.daysBetween(curDay, preRepayDay) - 1)/(dateMathUtil.daysBetween(curRepayDay, preRepayDay)
        )*interest));
};

/**
 * 等额本息
 * 实际利率
 */
CreditorCalculateService.prototype.creditorActualRate = function (loanMoney, rate, repayMonth, period, tranMoney, surMoney) {
    return Math.round(((loanMoney * (rate/12) - repayMonth) * (1 + Math.pow((rate/12),(period - 1))) + repayMonth)
        *tranMoney/surMoney/tranMoney*12);
}

/**
 * 等额本息
 * 受让人本金
 */
CreditorCalculateService.prototype.acceptmoney = function (money,total_price,moneyCur) {
    return Math.round((money/total_price)*moneyCur);
}

/**
 * 等额本息
 * 受让人当期利息
 */
CreditorCalculateService.prototype.acceptinterestCur = function (curRepayDay,curDay,preRepayDay,money,total_price,interest) {
    return Math.round((money/total_price)*interest*((dateMathUtil.daysBetween(curRepayDay, curDay) + 1)/(dateMathUtil.daysBetween(curRepayDay, preRepayDay))));
}

/**
 * 等额本息
 * 受让人利息
 */
CreditorCalculateService.prototype.acceptinterest = function (money,total_price,interest) {
    return Math.round((money/total_price)*interest);
}

var creditorCalculateService = new CreditorCalculateService();
module.exports = creditorCalculateService;