var dc = require('mcp_db').dc;


var CreditorUtilService = function () {
};

/**
 * 查询还款的当月的本金和利息
 * @param loan_id
 * @param period
 * @param all_period
 * @param cb
 */
CreditorUtilService.prototype.getMonInterCur = function (loan_id, period, all_period, cb) {
    var conn = dc.main.getConn();
    var sql = "SELECT * FROM loan_repay WHERE loan_id = " + loan_id + " AND period = " + period + " AND all_period = " + all_period + " ";
    conn.execute(sql, [], function (err, data) {
        cb(err, data[0]);
    });
    return;
}

module.exports = new CreditorUtilService();