'use strict';
var dc = require('mcp_db').dc;

var TaskService = function () {
};


TaskService.prototype.add = function (cond, cb) {
    var task = dc.mg.get("task");
    task.save(cond, [], function (err) {
        if (err) {
            cb(err);
        }
        cb(null);
    });
};


var taskService = new TaskService();
module.exports = taskService;
