'use strict';


var amqp = require('amqplib/callback_api');

var esut = require("easy_util");
var log = esut.log;

var config = require("mcp_config");
var prop = config.prop;

var MqSource = function () {
    var self = this;
};

MqSource.prototype.init = function (queue, cb) {
    var self = this;
    self.queue = queue;
    if (self.conn) {
        cb(null);
    } else {
        amqp.connect('amqp://'+prop.mq.host+':'+prop.mq.port, {'noDelay': true}, function (err, conn) {
            self.conn = conn;
            if (!err) {
                log.info('-----MQ连线初始化成功');
                process.once('SIGINT', function () {
                    self.conn.close();
                });
            }
            cb(err);
        });
    }
};

MqSource.prototype.bail = function (err, conn) {
    if (err) {
        conn.close();
    }
};


MqSource.prototype.send = function (queueSend, content, cb) {
    var self = this;
    self.conn.createChannel(function (err, ch) {
        if (err !== null) {
            cb(err);
            return self.bail(err, self.conn);
        }
        ch.assertQueue(self.queue, {durable: false}, function (err, ok) {
            if (err !== null) {
                cb(err);
                return self.bail(err, self.conn);
            }
            ch.consume(self.queue, function (msg) {
                cb(null, msg.content.toString());
                ch.ack(msg);
                ch.close();
            }, {noAck: false});
            log.info(" servce send:" + content);
            ch.sendToQueue(queueSend, new Buffer(content), {replyTo: self.queue})
        })
    })
};


var mqSource = new MqSource();
module.exports = mqSource;