var dc = require('mcp_db').dc;

var engine = require('mcp_engine');
var creditorDebxEngine = engine.creditorDebxEngine;

var CreditorService = function () {
};

/**
 * 获取当前还款日
 * @param loan_id
 */
CreditorService.prototype.getCurRepayDay = function (loan_id, cb) {
    var conn = dc.main.getConn();
    var sql = "SELECT * FROM loan_repay WHERE loan_id = " + loan_id + " AND status =  1 ORDER BY create_time LIMIT 1";
        conn.execute(sql, [], function (err, data) {
            cb(err, data[0]);
        });
    return;
};

/**
 * 购买债权处理
 * @param money
 * @param userID
 * @param accept_money
 * @param sur_money
 * @param id
 * @param status
 * @param create_time
 * @param creditor_loan_id
 * @param user_id
 * @param cb
 */
CreditorService.prototype.buyTran = function (money, userID, accept_money,sur_money,id,status,create_time,creditor_loan_id,user_id, loanRepayObj, creditorLoanObj,investObj,loanObj,curRepayDay,curDay,preRepayDay,creditorValueInvest, cb) {
    console.log("-------------->" + creditorLoanObj.type);
    var loan_id = loanRepayObj.loan_id;
    var i = loanRepayObj.period;
    var j = loanRepayObj.all_period;
    var conn = dc.main.getConn();
    var sql = "UPDATE account SET money = " + sur_money + " WHERE user_id = '" + userID + "' ";
    var sql2 = "insert INTO creditor_invest(id, money,status,create_time,user_id,creditor_loan_id,creditor_value) VALUES (" +
         id + "," + money + "," + status + ", '" + create_time + "'" + ", '" + user_id + "'" + ", '" + creditor_loan_id + "'" + "," + creditorValueInvest +
    ");";
    var sql3 = "SELECT SUM(interest) as countInterest FROM loan_repay WHERE loan_id = '" + loan_id + "' ";
    var sql4 = "";
    conn.execute(sql, [], function (err, data) {
        if(err) {
            console.log(err);
        }else {
            conn.execute(sql2, [], function (err, data) {
                if(err) {
                    console.log(err);
                }else {
                    conn.execute(sql3, [], function (err, data) {
                        var data = data[0].countInterest;
                        console.log("sql语句执行标的的总利息为：" + data);
                        if(err) {
                            console.log(err);
                        }else {
                            switch (creditorLoanObj.type) {
                                case 'debx' :
                                    //本金和利息更新操作
                                    creditorDebxEngine.debxCreditor(id,money,creditorLoanObj,i,j,loanObj,loanRepayObj,curRepayDay,curDay,preRepayDay,data,investObj,function (err) {
                                        //console.log(err);
                                    });
                                    break;

                                case 'ayfx':
                                    break;

                                default:
                            }
                            cb(err, null);
                        }
                    });
                }
            });
        }
    });
};

module.exports = new CreditorService();