var moment = require('moment');


var DateUtil = function () {
};
/**
 * 精确2位小数
 * @param x
 * @param num
 * @returns {number}
 */
DateUtil.prototype.twoPrecise  = function (data) {
    return Math.round(data * Math.pow(10, 2)) / Math.pow(10, 2);
}
/**
 * 获取日期的上个月
 * @param date
 */
DateUtil.prototype.dayNumOfMonth = function (date) {
    var arr = date.split('-');
    var year = arr[0]; //获取当前日期的年份
    var month = arr[1]; //获取当前日期的月份
    var day = arr[2]; //获取当前日期的日
    var days = new Date(year, month, 0);
    days = days.getDate(); //获取当前日期中月的天数
    var year2 = year;
    var month2 = parseInt(month) - 1;
    if (month2 == 0) {
        year2 = parseInt(year2) - 1;
        month2 = 12;
    }
    var day2 = day;
    var days2 = new Date(year2, month2, 0);
    days2 = days2.getDate();
    if (day2 > days2) {
        day2 = days2;
    }
    if (month2 < 10) {
        month2 = '0' + month2;
    }
    var t2 = year2 + '-' + month2 + '-' + day2;
    return t2;
};

/**
 * 获取2个日期相差的小时数
 * @type {DateUtil}
 */
DateUtil.prototype.hourNumOfDate = function (data1, data2) {
    var regTime = /(\d{4})-(\d{1,2})-(\d{1,2})( \d{1,2}:\d{1,2})/g;
    var interval = Math.abs(Date.parse(data1.replace(regTime, "$2-$3-$1$4")) - Date.parse(data2.replace(regTime, "$2-$3-$1$4")))/1000;
    var h = Math.floor(interval / 3600);
    var m = Math.floor(interval % 3600 / 60);
    var m2 = m/60;
    var h2 = h + m2;
    return Math.round(h2);
};

module.exports = new DateUtil();